INSERT INTO `boxes` VALUES ('1','<?php\r\n$pr_links = menu_primary_links();\r\nprint theme(\'links\', $pr_links, array(\'class\' => \'links primary-links\'));\r\n?>','Primary Navigation','3'),('3','<?php\r\n\r\nif (arg(0) == \'node\' && is_numeric(arg(1))) {\r\n    $node = node_load(arg(1));\r\n   \r\n      $nodeout = node_view($node);     \r\n\r\n     $toc = array();\r\n\r\n         // $i = 1;\r\n         // $matches[0][$i] -> Whole string matched\r\n        // $matches[1][$i] -> First heading level\r\n        // $matches[2][$i] -> Whole string of attributes\r\n        // $matches[3][$i] -> id attibute, used for anchor\r\n        // $matches[4][$i] -> Text of id attribute\r\n        // $matches[5][$i] -> Text inside of h tag\r\n        // $matches[6][$i] -> Close heading level, should be equal to open level\r\n        $matches = array();\r\n\r\n        //get all headers of current level, case insensitive\r\n        $pattern = \'/<h([1-4])( .*?(id=\"([^\"]+)\" ?.*?))?>(.*?)<\\/h([1-4])/is\';\r\n         \r\n        $matches = array();\r\n        preg_match_all($pattern, $nodeout, $matches, PREG_PATTERN_ORDER);\r\n\r\n        $anchors = array();\r\n\r\n        for ($i = 1; $i < sizeof($matches[0]); $i++) {\r\n\r\n          // Strip HTML and non alphanumerics\r\n          $level = $matches[1][$i];\r\n          $heading = strip_tags($matches[5][$i]);\r\n          $anchor = $matches[4][$i];\r\n          array_push($toc, array(\r\n            \'level\' => $level,\r\n            \'heading\' => $heading,\r\n            \'anchor\' => $anchor)\r\n          );\r\n        }\r\n\r\n        // Build HTML for the Table of Contents\r\n        $toc_html = \"<div id=\\\"toc-parent\\\">\\n\";\r\n        $toc_html .= \"<div display=\\\"display: block;\\\" id=\\\"toc\\\">\";\r\n        $depth = 2;\r\n        $num = 1;\r\n        foreach ($toc as $index=>$title) {\r\n          // process nested lists\r\n          $curdepth = $title[\'level\'];\r\n          if ($curdepth <= 4) {\r\n            // Close list items but not before no items have been added\r\n            if ($curdepth == $depth && $index != 0) $toc_html .= \"\\n\";\r\n            // Be sure to deal with skipping between non-adjacent h levels\r\n            while ($curdepth != $depth) {\r\n              if ($curdepth > $depth) {\r\n                $toc_html .= \"\";\r\n                $depth++;\r\n              }\r\n              else if ($curdepth < $depth) {\r\n                $toc_html .= \"\";\r\n                $depth--;\r\n                if ($curdepth == $depth) $toc_html .= \"\";\r\n              }\r\n            }\r\n\r\n            // insert the anchors element and check not letting the link title to output as content\r\n          if ($curdepth == $level) {\r\n            $toc_html .= \"\\t<div class=\\\"tocline\\\"><a href=\\\"#\".$title[\'heading\'].\"\\\"><span class=\\\"number\\\">$num</span> \".$title[\'heading\'].\"</a></div>\";\r\n             $num++;\r\n}\r\n          }\r\n        }\r\n        // Did we recurse back out? If not, close open lists.\r\n        while ($depth > 2) {\r\n          $toc_html .= \"</div>\";\r\n          $depth = $depth -1;\r\n        }\r\n        $toc_html .= \"</div>\";\r\n       \r\n        $toc_html .= \"</div>\";\r\n\r\n\r\nprint $toc_html; \r\n}\r\n?>\r\n','Table of contents','3');
