INSERT INTO `locales_source` VALUES ('879',' profiles/default/default.profile:111','default','Story','6.15'),('880',' profiles/default/default.profile:113','default','A <em>story</em>, similar in form to a <em>page</em>, is ideal for creating and displaying content that informs or engages website visitors. Press releases, site announcements, and informal blog-like entries may all be created with a <em>story</em> entry. By default, a <em>story</em> entry is automatically featured on the site\'s initial home page, and provides the ability to post comments.','6.15'),('881',' misc/drupal.js:0','default','An error occurred. \n@uri\n@text','none'),('882',' misc/drupal.js:0','default','An error occurred. \n@uri\n(no information available).','none'),('883',' misc/drupal.js:0','default','An HTTP error @status occurred. \n@uri','none'),('884',' modules/aggregator/aggregator-feed-source.tpl.php:30','default','URL:','none'),('885',' modules/aggregator/aggregator-feed-source.tpl.php:33','default','Updated:','none'),('886',' modules/aggregator/aggregator-item.tpl.php:42,  modules/aggregator/aggregator.pages.inc:63,  modules/aggregator/aggregator.module:151','default','Categories','none'),('887',' modules/aggregator/aggregator-summary-items.tpl.php:23','default','More','none'),('888',' modules/aggregator/aggregator.admin.inc:25','default','Feed overview','none'),('889',' modules/aggregator/aggregator.admin.inc:27;38','default','Items','none'),('890',' modules/aggregator/aggregator.admin.inc:27','default','Last update','none'),('891',' modules/aggregator/aggregator.admin.inc:27','default','Next update','none'),('892',' modules/aggregator/aggregator.admin.inc:30','default','%time left','none'),('893',' modules/aggregator/aggregator.admin.inc:30','default','remove items','none'),('894',' modules/aggregator/aggregator.admin.inc:30','default','update items','none'),('895',' modules/aggregator/aggregator.admin.inc:36','default','Category overview','none'),('896',' modules/aggregator/agg