INSERT INTO `locales_source` VALUES ('929',' modules/aggregator/aggregator.admin.inc:30;41,  modules/aggregator/aggregator.module:923','default','@count items','none'),('930',' modules/aggregator/aggregator.pages.inc:68','default','aggregator - @title','none'),('931',' modules/aggregator/aggregator.pages.inc:175','default','Save categories','none'),('932',' modules/aggregator/aggregator.pages.inc:185','default','You are not allowed to categorize this feed item.','none'),('933',' modules/aggregator/aggregator.pages.inc:203','default','The categories have been saved.','none'),('934',' modules/aggregator/aggregator.pages.inc:228,  modules/aggregator/aggregator.module:185;213','default','Categorize','none'),('935',' modules/aggregator/aggregator.pages.inc:262','default','%ago ago','none'),('936',' modules/aggregator/aggregator.pages.inc:293,  modules/aggregator/aggregator.module:164','default','OPML feed','none'),('937',' modules/aggregator/aggregator.pages.inc:377','default','@site_name - aggregated feeds in category @title','none'),('938',' modules/aggregator/aggregator.pages.inc:377','default','@site_name - aggregated feeds','none'),('939',' modules/aggregator/aggregator.pages.inc:381','default','@site_name aggregator','none'),('940',' modules/aggregator/aggregator.pages.inc:459','default','%age old','none'),('941',' modules/aggregator/aggregator.pages.inc:477','default','!title feed','none'),('942',' modules/aggregator/aggregator.module:15','default','The aggregator is a powerful on-site syndicator and news reader that gathers fresh content from RSS-, RDF-, and Atom-based feeds made available across the web. Thousands of sites (particularly news sites and blogs) publish their latest headlines and posts in feeds, using a number of standardized XML-based formats. Formats supported by the aggregator include <a href=\"@rss\">RSS</a>, <a href=\"@rdf\">RDF</a>, and <a href=\"@atom\">Atom</a>.','none'),('943',' modules/aggregator/aggregator.module:16','default','Feeds contain feed items, or individual posts published by the site providing the feed. Feeds may be grouped in categories, generally by topic. Users view feed items in the <a href=\"@aggregator\">main aggregator display</a> or by <a href=\"@aggregator-sources\">their source</a>. Administrators can <a href=\"@feededit\">add, edit and delete feeds</a> and choose how often to check each feed for newly updated items. The most recent items in either a feed or category can be displayed as a block through the <a href=\"@admin-block\">blocks administration page</a>. A <a href=\"@aggregator-opml\">machine-readable OPML file</a> of all feeds is available. A correctly configured <a href=\"@cron\">cron maintenance task</a> is required to update feeds automatically.','none');
