INSERT INTO `locales_source` VALUES ('1143',' modules/blog/blog.module:67','default','When using the aggregator module an automatic <em>blog it</em> icon is displayed next to the items in a feed\'s <em>latest items</em> block. Clicking this icon populates a <em>blog entry</em> with a title (the title of the feed item) and body (a link to the source item on its original site and illustrative content suitable for use in a block quote). Blog authors can use this feature to easily comment on items of interest that appear in aggregator feeds from other sites. To use this feature, be sure to <a href=\"@modules\">enable</a> the aggregator module, <a href=\"@feeds\">add and configure</a> a feed from another site, and <a href=\"@blocks\">position</a> the feed\'s <em>latest items</em> block.','none'),('1144',' modules/blog/blog.module:68','default','For more information, see the online handbook entry for <a href=\"@blog\">Blog module</a>.','none'),('1145',' modules/blog/blog.module:108;137;152;160','default','Blogs','none'),('1146',' modules/blog/blog.module:122','default','@username\'s blog','none'),('1147',' modules/blog/blog.module:178;187','default','Recent blog posts','none'),('1148',' modules/blog/blog.module:186','default','Read the latest blog entries.','none'),('1149',' modules/blog/blog.module:26','default','create blog entries','none'),('1150',' modules/blog/blog.module:26','default','delete own blog entries','none'),('1151',' modules/blog/blog.module:26','default','delete any blog entry','none'),('1152',' modules/blog/blog.module:26','default','edit own blog entries','none'),('1153',' modules/blog/blog.module:26','default','edit any blog entry','none'),('1154',' modules/blog/blog.module:144','default','My blog','none'),('1155',' modules/blog/blog.module:0','default','blog','none'),('1156',' modules/book/book-navigation.tpl.php:40','default','‹ ','none'),('1157',' modules/book/book-navigation.tpl.php:43','default','Go to parent page','none'),('1158',' modules/book/