INSERT INTO `locales_source` VALUES ('1385',' modules/contact/contact.admin.inc:156','default','Additional information','none'),('1386',' modules/contact/contact.admin.inc:157 modules/contact/contact.pages.inc:41','default','You can leave a message using the contact form below.','none'),('1387',' modules/contact/contact.admin.inc:158','default','Information to show on the <a href=\"@form\">contact page</a>. Can be anything from submission guidelines to your postal address or telephone number.','none'),('1388',' modules/contact/contact.admin.inc:161','default','Hourly threshold','none'),('1389',' modules/contact/contact.admin.inc:164','default','The maximum number of contact form submissions a user can perform per hour.','none'),('1390',' modules/contact/contact.admin.inc:168','default','Enable personal contact form by default','none'),('1391',' modules/contact/contact.admin.inc:170','default','Default status of the personal contact form for new users.','none'),('1392',' modules/contact/contact.admin.inc:115','default','Contact form: category %category added.','none'),('1393',' modules/contact/contact.admin.inc:121','default','Contact form: category %category updated.','none'),('1394',' modules/contact/contact.admin.inc:148','default','Contact form: category %category deleted.','none'),('1395',' modules/contact/contact.pages.inc:17','default','You cannot send more than %number messages per hour. Please try again later.','none'),('1396',' modules/contact/contact.pages.inc:49','default','Your e-mail address','none'),('1397',' modules/contact/contact.pages.inc:88;197','default','Send yourself a copy.','none'),('1398',' modules/contact/contact.pages.inc:99','default','The contact form has not been configured. <a href=\"@add\">Add one or more categories</a> to the form.','none'),('1399',' modules/contact/contact.pages.inc:109','default','You must select a valid category.','none'),('1400',' modules/contact/contact.pages.inc:112','default','You must enter a valid e-mail address.','none'),('1401',' modules/contact/contact.pages.inc:147','default','Your message has been sent.','none');
INSERT INTO `locales_source` VALUES ('1402',' modules/contact/contact.pages.inc:161','default','You need to provide a valid e-mail address to contact other users. Please update your <a href=\"@url\">user information</a> and try again.','none'),('1403',' modules/contact/contact.pages.inc:164','default','You cannot contact more than %number users per hour. Please try again later.','none'),('1404',' modules/contact/contact.pages.inc:179','default','From','none'),('1405',' modules/contact/contact.pages.inc:183','default','To','none'),('1406',' modules/contact/contact.pages.inc:232','default','The message has been sent.','none'),('1407',' modules/contact/contact.pages.inc:146','default','%name-from sent an e-mail regarding %category.','none'),('1408',' modules/contact/contact.pages.inc:231','default','%name-from sent %name-to an e-mail.','none'),('1409',' modules/contact/contact.module:15','default','The contact module facilitates communication via e-mail, by allowing your site\'s visitors to contact one another (personal contact forms), and by providing a simple way to direct messages to a set of administrator-defined recipients (the <a href=\"@contact\">contact page</a>). With either form, users specify a subject, write their message, and (optionally) have a copy of their message sent to their own e-mail address.','none'),('1410',' modules/contact/contact.module:16','default','Personal contact forms allow users to be contacted via e-mail, while keeping recipient e-mail addresses private. Users may enable or disable their personal contact forms by editing their <em>My account</em> page. If enabled, a <em>Contact</em> tab leading to their personal contact form is available on their user profile. Site administrators have access to all personal contact forms (even if they have been disabled). The <em>Contact</em> tab is only visible when viewing another user\'s profile (users do not see their own <em>Contact</em> tab).','none'),('1411',' modules/contact/contact.module:17','default','The <a href=\"@contact\">contact page</a> provides a simple form for visitors to leave comments, feedback, or other requests. Messages are routed by selecting a category from a list of administrator-defined options; each category has its own set of e-mail recipients. Common categories for a business site include, for example, \"Website feedback\" (messages are forwarded to web site administrators) and \"Product information\" (messages are forwarded to members of the sales department). The actual e-mail addresses defined within a category are not displayed. Only users in roles with the <em>access site-wide contact form</em> permission may access the <a href=\"@contact\">contact page</a>.','none');
INSERT INTO `locales_source` VALUES ('1412',' modules/contact/contact.module:18','default','A link to your site\'s <a href=\"@contact\">contact page</a> from the main <em>Navigation</em> menu is created, but is disabled by default. Create a similar link on another menu by adding a menu item pointing to the path \"contact\"','none'),('1413',' modules/contact/contact.module:19','default','Customize the <a href=\"@contact\">contact page</a> with additional information (like physical location, mailing address, and telephone number) using the <a href=\"@contact-settings\">contact form settings page</a>. The <a href=\"@contact-settings\">settings page</a> also provides configuration options for the maximum number of contact form submissions a user may perform per hour, and the default status of users\' personal contact forms.','none'),('1414',' modules/contact/contact.module:20','default','For more information, see the online handbook entry for <a href=\"@contact\">Contact module</a>.','none'),('1415',' modules/contact/contact.module:23','default','This page lets you set up <a href=\"@form\">your site-wide contact form</a>. To do so, add one or more categories. You can associate different recipients with each category to route e-mails to different people. For example, you can route website feedback to the webmaster and direct product information requests to the sales department. On the <a href=\"@settings\">settings page</a>, you can customize the information shown above the contact form. This can be useful to provide additional contact information such as your postal address and telephone number.','none'),('1416',' modules/contact/contact.module:25','default','The menu item can be customized and configured only once the menu module has been <a href=\"@modules-page\">enabled</a>.','none'),('1417',' modules/contact/contact.module:30','default','The contact module also adds a <a href=\"@menu-settings\">menu item</a> (disabled by default) to the navigation block.','none'),('1418',' modules/contact/contact.module:140','default','Contact settings','none');
INSERT INTO `locales_source` VALUES ('1419',' modules/contact/contact.module:145','default','Personal contact form','none'),('1420',' modules/contact/contact.module:147','default','Allow other users to contact you by e-mail via <a href=\"@url\">your personal contact form</a>. Note that while your e-mail address is not made public to other members of the community, privileged users such as site administrators are able to contact you even if you choose not to enable this feature.','none'),('1421',' modules/contact/contact.module:168;174','default','[!category] !subject','none'),('1422',' modules/contact/contact.module:169','default','!name sent a message using the contact form at !form.','none'),('1423',' modules/contact/contact.module:183','default','!name (!name-url) has sent you a message via your contact form (!form-url) at !site.','none'),('1424',' modules/contact/contact.module:184','default','If you don\'t want to receive such e-mails, you can change your settings at !url.','none'),('1425',' modules/contact/contact.module:185','default','Message:','none'),('1426',' modules/contact/contact.module:39','default','access site-wide contact form','none'),('1427',' modules/contact/contact.module:39','default','administer site-wide contact form','none'),('1428',' modules/contact/contact.module:46','default','Contact form','none'),('1429',' modules/contact/contact.module:47','default','Create a system contact form and set up categories for the form to use.','none'),('1430',' modules/contact/contact.module:67','default','Edit contact category','none'),('1431',' modules/contact/contact.module:74','default','Delete contact','none'),('1432',' modules/contact/contact.module:0','default','contact','none'),('1433',' modules/contact/contact.install:29','default','Contact form category settings.','none'),('1434',' modules/contact/contact.install:35','default','Primary Key: Unique category ID.','none'),('1435',' modules/contact/contact.install:42','default','Category name.','none