INSERT INTO `locales_source` VALUES ('1580',' modules/filter/filter.module:663','default','How to deal with HTML tags in user-contributed content. If set to \"Strip disallowed tags\", dangerous tags are removed (see below). If set to \"Escape tags\", all HTML is escaped and presented as it was typed.','none'),('1581',' modules/filter/filter.module:671','default','If \"Strip disallowed tags\" is selected, optionally specify tags which should not be stripped. JavaScript event attributes are always stripped.','none'),('1582',' modules/filter/filter.module:675','default','Display HTML help','none'),('1583',' modules/filter/filter.module:677','default','If enabled, Drupal will display some basic HTML help in the long filter tips.','none'),('1584',' modules/filter/filter.module:681','default','Spam link deterrent','none'),('1585',' modules/filter/filter.module:683','default','If enabled, Drupal will add rel=\"nofollow\" to all links, as a measure to reduce the effectiveness of spam links. Note: this will also prevent valid links from being followed by search engines, therefore it is likely most effective when enabled for anonymous users.','none'),('1586',' modules/filter/filter.module:720','default','Maximum link text length','none'),('1587',' modules/filter/filter.module:723','default','URLs longer than this number of characters will be truncated to prevent long strings that break formatting. The link itself will be retained; just the text portion of the link will be truncated.','none'),('1588',' modules/filter/filter.module:153','default','administer filters','6.15'),('1589',' modules/filter/filter.module:71','default','Input formats','6.15'),('1590',' modules/filter/filter.module:72','default','Configure how content input by users is filtered, including allowed HTML tags. Also allows enabling of module-provided filters.','6.15'),('1591',' modules/filter/filter.module:90','default','Delete input format','6.15'),('1592',' modules/filter/filter.module:97','default','Compo