INSERT INTO `locales_source` VALUES ('1671',' modules/forum/forum.module:322','default','create forum topics','none'),('1672',' modules/forum/forum.module:322','default','delete own forum topics','none'),('1673',' modules/forum/forum.module:322','default','delete any forum topic','none'),('1674',' modules/forum/forum.module:322','default','edit own forum topics','none'),('1675',' modules/forum/forum.module:322','default','edit any forum topic','none'),('1676',' modules/forum/forum.module:322','default','administer forums','none'),('1677',' modules/forum/forum.module:86;93 modules/forum/forum.install:27;116','default','Forums','none'),('1678',' modules/forum/forum.module:94','default','Control forums and their hierarchy and change forum settings.','none'),('1679',' modules/forum/forum.module:106','default','Add container','none'),('1680',' modules/forum/forum.module:114','default','Add forum','none'),('1681',' modules/forum/forum.module:136','default','Edit container','none'),('1682',' modules/forum/forum.module:143','default','Edit forum','none'),('1683',' modules/forum/forum.install:69','default','Stores the relationship of nodes to forum terms.','none'),('1684',' modules/forum/forum.install:76','default','The {node}.nid of the node.','none'),('1685',' modules/forum/forum.install:90','default','The {term_data}.tid of the forum term assigned to the node.','none'),('1686',' modules/help/help.admin.inc:15','default','Help topics','none'),('1687',' modules/help/help.admin.inc:15','default','Help is available on the following items:','none'),('1688',' modules/help/help.admin.inc:30','default','No help is available for module %module.','none'),('1689',' modules/help/help.admin.inc:41','default','@module administration pages','none'),('1690',' modules/help/help.module:40','default','This guide provides context sensitive help on the use and configuration of <a href=\"@drupal\">Drupal</a> and its modules, and is a supplement to the more extensive online <a href=\"@handbook\">Drupal handbook</a>. The online handbook may contain more up-to-date information, is annotated with helpful user-contributed comments, and serves as the definitive reference point for all Drupal documentation.','none');
INSERT INTO `locales_source` VALUES ('1691',' modules/help/help.module:43','default','The help module provides context sensitive help on the use and configuration of <a href=\"@drupal\">Drupal</a> and its modules, and is a supplement to the more extensive online <a href=\"@handbook\">Drupal handbook</a>. The online handbook may contain more up-to-date information, is annotated with helpful user-contributed comments, and serves as the definitive reference point for all Drupal documentation.','none'),('1692',' modules/help/help.module:44','default','For more information, see the online handbook entry for <a href=\"@help\">Help module</a>.','none'),('1693',' modules/help/help.module:0','default','help','6.15'),('1694',' modules/locale/locale.module:35','default','The locale module allows your Drupal site to be presented in languages other than the default English, a defining feature of multi-lingual websites. The locale module works by examining text as it is about to be displayed: when a translation of the text is available in the language to be displayed, the translation is displayed rather than the original text. When a translation is unavailable, the original text is displayed, and then stored for later review by a translator.','none'),('1695',' modules/locale/locale.module:36','default','Beyond translation of the Drupal interface, the locale module provides a feature set tailored to the needs of a multi-lingual site. Language negotiation allows your site to automatically change language based on the domain or path used for each request. Users may (optionally) select their preferred language on their <em>My account</em> page, and your site can be configured to honor a web browser\'s preferred language settings. Your site content can be created in (and translated to) any enabled language, and each post may have a language-appropriate alias for each of its translations. The locale module works in concert with the <a href=\"@content-help\">content translation module</a> to manage translated content.','none'),('1696',' modules/locale/locale.module:37','default','Translations may be provided by:','none');
INSERT INTO `locales_source` VALUES ('1697',' modules/locale/locale.module:38','default','translating the original text via the locale module\'s integrated web interface, or','none'),('1698',' modules/locale/locale.module:39','default','importing files from a set of existing translations, known as a translation package. A translation package enables the display of a specific version of Drupal in a specific language, and contain files in the Gettext Portable Object (<em>.po</em>) format. Although not all languages are available for every version of Drupal, translation packages for many languages are available for download from the <a href=\"@translations\">Drupal translation page</a>.','none'),('1699',' modules/locale/locale.module:40','default','If an existing translation package does not meet your needs, the Gettext Portable Object (<em>.po</em>) files within a package may be modified, or new <em>.po</em> files may be created, using a desktop Gettext editor. The locale module\'s <a href=\"@import\">import</a> feature allows the translated strings from a new or modified <em>.po</em> file to be added to your site. The locale module\'s <a href=\"@export\">export</a> feature generates files from your site\'s translated strings, that can either be shared with others or edited offline by a Gettext translation editor.','none'),('1700',' modules/locale/locale.module:41','default','For more information, see the online handbook entry for <a href=\"@locale\">Locale module</a>.','none'),('1701',' modules/locale/locale.module:44','default','This page provides an overview of your site\'s enabled languages. If multiple languages are available and enabled, the text on your site interface may be translated, registered users may select their preferred language on the <em>My account</em> page, and site authors may indicate a specific language when creating posts. The site\'s default language is used for anonymous visitors and for users who have not selected a preferred language.','6.15'),('1702',' modules/locale/locale.module:45','default','For each language available on the site, use the <em>edit</em> link to configure language details, including name, an optional language-specific path or domain, and whether the language is natively presented either left-to-right or right-to-left. These languages also appear in the <em>Language</em> selection when creating a post of a content type with multilingual support.','6.15');
INSERT INTO `locales_source` VALUES ('1703',' modules/locale/locale.module:49','default','Add all languages to be supported by your site. If your desired language is not available in the <em>Language name</em> drop-down, click <em>Custom language</em> and provide a language code and other details manually. When providing a language code manually, be sure to enter a standardized language code, since this code may be used by browsers to determine an appropriate display language.','6.15'),('1704',' modules/locale/locale.module:51','default','Language negotiation settings determine the site\'s presentation language. Available options include:','6.15'),('1705',' modules/locale/locale.module:52','default','<strong>None.</strong> The default language is used for site presentation, though users may (optionally) select a preferred language on the <em>My Account</em> page. (User language preferences will be used for site e-mails, if available.)','6.15'),('1706',' modules/locale/locale.module:53','default','<strong>Path prefix only.</strong> The presentation language is determined by examining the path for a language code or other custom string that matches the path prefix (if any) specified for each language. If a suitable prefix is not identified, the default language is used. <em>Example: \"example.com/de/contact\" sets presentation language to German based on the use of \"de\" within the path.</em>','6.15'),('1707',' modules/locale/locale.module:54','default','<strong>Path prefix with language fallback.</strong> The presentation language is determined by examining the path for a language code or other custom string that matches the path prefix (if any) specified for each language. If a suitable prefix is not identified, the display language is determined by the user\'s language preferences from the <em>My Account</em> page, or by the browser\'s language settings. If a presentation language cannot be determined, the default language is used.','6.15'),('1708',' modules/locale/locale.module:55','default','<strong>Domain name only.</strong> The presentation language is determined by examining the domain used to access the site, and comparing it to the language domain (if any) specified for each language. If a match is not identified, the default language is used. <em>Example: \"http://de.example.com/contact\" sets presentation language to German based on the use of \"http://de.example.com\" in the domain.</em>','6.15');
INSERT INTO `locales_source` VALUES ('1709',' modules/locale/locale.module:56','default','The path prefix or domain name for a language may be set by editing the <a href=\"@languages\">available languages</a>. In the absence of an appropriate match, the site is displayed in the <a href=\"@languages\">default language</a>.','6.15'),('1710',' modules/locale/locale.module:59','default','This page provides an overview of available translatable strings. Drupal displays translatable strings in text groups; modules may define additional text groups containing other translatable strings. Because text groups provide a method of grouping related strings, they are often used to focus translation efforts on specific areas of the Drupal interface.','6.15'),('1711',' modules/locale/locale.module:60','default','Review the <a href=\"@languages\">languages page</a> for more information on adding support for additional languages.','6.15'),('1712',' modules/locale/locale.module:63','default','This page imports the translated strings contained in an individual Gettext Portable Object (<em>.po</em>) file. Normally distributed as part of a translation package (each translation package may contain several <em>.po</em> files), a <em>.po</em> file may need to be imported after off-line editing in a Gettext translation editor. Importing an individual <em>.po</em> file may be a lengthy process.','none'),('1713',' modules/locale/locale.module:64','default','Note that the <em>.po</em> files within a translation package are imported automatically (if available) when new modules or themes are enabled, or as new languages are added. Since this page only allows the import of one <em>.po</em> file at a time, it may be simpler to download and extract a translation package into your Drupal installation directory and <a href=\"@language-add\">add the language</a> (which automatically imports all <em>.po</em> files within the package). Translation packages are available for download on the <a href=\"@translations\">Drupal translation page</a>.','none');
INSERT INTO `locales_source` VALUES ('1714',' modules/locale/locale.module:67','default','This page exports the translated strings used by your site. An export file may be in Gettext Portable Object (<em>.po</em>) form, which includes both the original string and the translation (used to share translations with others), or in Gettext Portable Object Template (<em>.pot</em>) form, which includes the original strings only (used to create new translations with a Gettext translation editor).','none'),('1715',' modules/locale/locale.module:69','default','This page allows a translator to search for specific translated and untranslated strings, and is used when creating or editing translations. (Note: For translation tasks involving many strings, it may be more convenient to <a href=\"@export\">export</a> strings for off-line editing in a desktop Gettext translation editor.) Searches may be limited to strings found within a specific text group or in a specific language.','none'),('1716',' modules/locale/locale.module:72','default','This block is only shown if <a href=\"@languages\">at least two languages are enabled</a> and <a href=\"@configuration\">language negotiation</a> is set to something other than <em>None</em>.','none'),('1717',' modules/locale/locale.module:198','default','Built-in interface','6.15'),('1718',' modules/locale/locale.module:224','default','Language settings','none'),('1719',' modules/locale/locale.module:235','default','This account\'s default language for e-mails, and preferred language for site presentation.','none'),('1720',' modules/locale/locale.module:235','default','This account\'s default language for e-mails.','none'),('1721',' modules/locale/locale.module:264','default','Multilingual support','6.15'),('1722',' modules/locale/locale.module:267','default','Enable multilingual support for this content type. If enabled, a language selection field will be added to the editing form, allowing you to select from one of the <a href=\"!languages\">enabled languages</a>. If disabled, new posts are saved with the default language. Existing content will not be affected by changing this option.','6.15');
