INSERT INTO `locales_source` VALUES ('2006',' modules/node/node.module:1731','default','RSS','none'),('2007',' modules/node/node.module:1735','default','<h1 class=\"title\">Welcome to your new Drupal website!</h1><p>Please follow these steps to set up and start using your website:</p>','none'),('2008',' modules/node/node.module:1738','default','<strong>Configure your website</strong> Once logged in, visit the <a href=\"@admin\">administration section</a>, where you can <a href=\"@config\">customize and configure</a> all aspects of your website.','none'),('2009',' modules/node/node.module:1739','default','<strong>Enable additional functionality</strong> Next, visit the <a href=\"@modules\">module list</a> and enable features which suit your specific needs. You can find additional modules in the <a href=\"@download_modules\">Drupal modules download section</a>.','none'),('2010',' modules/node/node.module:1740','default','<strong>Customize your website design</strong> To change the \"look and feel\" of your website, visit the <a href=\"@themes\">themes section</a>. You may choose from one of the included themes or download additional themes from the <a href=\"@download_themes\">Drupal themes download section</a>.','none'),('2011',' modules/node/node.module:1741','default','<strong>Start posting content</strong> Finally, you can <a href=\"@content\">create content</a> for your website. This message will disappear once you have promoted a post to the front page.','none'),('2012',' modules/node/node.module:1743','default','For more information, please refer to the <a href=\"@help\">help section</a>, or the <a href=\"@handbook\">online Drupal handbooks</a>. You may also post at the <a href=\"@forum\">Drupal forum</a>, or view the wide range of <a href=\"@support\">other support options</a> available.','none'),('2013',' modules/node/node.module:1815;1867','default','Advanced search','none'),('2014',' modules/node/node.module:1826','default','Containing any of the words','no