INSERT INTO `locales_source` VALUES ('3449',' modules/user/user.module:1545','default','The e-mail address %email has been denied access.','none'),('3450',' modules/user/user.module:1625','default','Account details for !username at !site','6.15'),('3451',' modules/user/user.module:1627','default','!username,\n\nThank you for registering at !site. You may now log in to !login_uri using the following username and password:\n\nusername: !username\npassword: !password\n\nYou may also log in by clicking on this link or copying and pasting it in your browser:\n\n!login_url\n\nThis is a one-time login, so it can be used only once.\n\nAfter logging in, you will be redirected to !edit_uri so you can change your password.\n\n\n--  !site team','6.15'),('3452',' modules/user/user.module:1629','default','An administrator created an account for you at !site','6.15'),('3453',' modules/user/user.module:1631','default','!username,\n\nA site administrator at !site has created an account for you. You may now log in to !login_uri using the following username and password:\n\nusername: !username\npassword: !password\n\nYou may also log in by clicking on this link or copying and pasting it in your browser:\n\n!login_url\n\nThis is a one-time login, so it can be used only once.\n\nAfter logging in, you will be redirected to !edit_uri so you can change your password.\n\n\n--  !site team','6.15'),('3454',' modules/user/user.module:1634','default','Account details for !username at !site (pending admin approval)','6.15'),('3455',' modules/user/user.module:1636','default','!username,\n\nThank you for registering at !site. Your application for an account is currently pending approval. Once it has been approved, you will receive another e-mail containing information about how to log in, set your password, and other details.\n\n\n--  !site team','6.15'),('3456',' modules/user/user.module:1638','default','!username has applied for an account.\n\n!edit_uri','none'),('3457',' modules/user/user.module:1640','default','Replacement login information for !username at !site','6.15');
INSERT INTO `locales_source` VALUES ('3458',' modules/user/user.module:1642','default','!username,\n\nA request to reset the password for your account has been made at !site.\n\nYou may now log in to !uri_brief by clicking on this link or copying and pasting it in your browser:\n\n!login_url\n\nThis is a one-time login, so it can be used only once. It expires after one day and nothing will happen if it\'s not used.\n\nAfter logging in, you will be redirected to !edit_uri so you can change your password.','6.15'),('3459',' modules/user/user.module:1644','default','Account details for !username at !site (approved)','6.15'),('3460',' modules/user/user.module:1646','default','!username,\n\nYour account at !site has been activated.\n\nYou may now log in by clicking on this link or copying and pasting it in your browser:\n\n!login_url\n\nThis is a one-time login, so it can be used only once.\n\nAfter logging in, you will be redirected to !edit_uri so you can change your password.\n\nOnce you have set your own password, you will be able to log in to !login_uri in the future using:\n\nusername: !username\n','6.15'),('3461',' modules/user/user.module:1648','default','Account details for !username at !site (blocked)','6.15'),('3462',' modules/user/user.module:1650','default','!username,\n\nYour account on !site has been blocked.','6.15'),('3463',' modules/user/user.module:1652','default','Account details for !username at !site (deleted)','6.15'),('3464',' modules/user/user.module:1654','default','!username,\n\nYour account on !site has been deleted.','6.15'),('3465',' modules/user/user.module:1714','default','Unblock the selected users','6.15'),('3466',' modules/user/user.module:1718','default','Block the selected users','6.15'),('3467',' modules/user/user.module:1722','default','Delete the selected users','6.15'),('3468',' modules/user/user.module:1742','default','Add a role to the selected users','6.15'),('3469',' modules/user/user.module:1745','default','Remove a role from